import React, {useEffect, useState} from 'react';
import './App.scss';
import {Post} from "./Post";
import { db } from './firebase'

function App() {
    const [posts, setPosts] = useState([
        {
            userName: 'alex_alex-androv',
            caption: 'Wooow!! heey cozy maaan!',
            imageUrl: 'https://digitalsynopsis.com/wp-content/uploads/2019/08/beautiful-illustrations-design-inspiration-37.png'
        },
        {
            userName: 'alex_drow',
            caption: 'Wooow!! heey damn maaan!',
            imageUrl: 'https://digitalsynopsis.com/wp-content/uploads/2019/08/beautiful-illustrations-design-inspiration-37.png'
        }
    ])


    useEffect(() => {
        db.collection('posts').onSnapshot(snapshot => {
            setPosts()
        })
    }, [])

    return (
        <div className="app">
            <header className='app__header'>
                <img
                    className='app__header-image'
                    src="https://logos-world.net/wp-content/uploads/2020/04/Instagram-Logo.png"
                    alt="logo"/>
            </header>
            {
                posts.map(el => (
                    <React.Fragment key={el.userName}>
                        <Post
                            userName={el.userName}
                            caption={el.caption}
                            imageUrl={el.imageUrl}
                        />
                    </React.Fragment>
                ))
            }
        </div>
    );
}

export default App;
